using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;public class PlayerHealth : MonoBehaviour{    public Animator animator;    public int maxHealth = 100;    public int currentHealth;    public GameObject healthBar;
    public AnimationEvents events;    public GameObject gameManager;    // Start is called before the first frame update    void Start()    {        healthBar.GetComponent<Slider>().maxValue = maxHealth;
        events = gameObject.GetComponent<AnimationEvents>();        setFullHealth();    }    // Update is called once per frame    void Update()    {    }    public void TakeDamage(int damage)    {        currentHealth -= damage;        DisplayHealth();        if (currentHealth <= 0)        {            animator.SetTrigger("Dead");            events.isAttacking = false;            GetComponent<PlayerCombatMelee>().DisableCombo();
            // Disable movement
            GetComponent<PlayerCombatMelee>().enabled = false;            GetComponent<PlayerMovement>().enabled = false;        }    }    void DisplayHealth()    {        healthBar.GetComponent<Slider>().value = currentHealth;    }    public void setFullHealth()    {        currentHealth = maxHealth;        DisplayHealth();    }    private void OnTriggerEnter2D(Collider2D collider)    {        if (collider.transform.parent.gameObject.name == "Beer")        {            currentHealth = maxHealth;            DisplayHealth();            collider.transform.parent.gameObject.SetActive(false);        }    }    private void setDeath()    {        gameManager.GetComponent<GamePause>().setDeath(true);    }}